<script setup>
import { ref, computed } from "vue";
import { useCartStore } from "@/stores/cartStore";

const cartStore = useCartStore();

const produto = ref({
  nome: "Cadeira do Rei",
  preco: 23.0,
  variacoes: [
    {
      id: 1,
      nome: "Vermelho",
      cor: "red",
      emEstoque: false,
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 2,
      nome: "Azul",
      cor: "blue",
      emEstoque: true,
      quantidade: 12,
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 3,
      nome: "Verde",
      cor: "green",
      emEstoque: true,
      quantidade: 8,
      image:
        "data:image/jpeg;base64,",
    },
  ],
});

const produtoAtivo = ref(produto.value.variacoes[0]);

const mudarProdutoAtivo = (variante) => {
  produtoAtivo.value = variante;
};

const adicionarAoCarrinho = () => {
  console.log("Produto adicionado ao carrinho:", produtoAtivo.value);
  if (!produtoAtivo.value.emEstoque) {
    alert("Produto fora de estoque");
    return;
  }
  cartStore.increment();
};

const precoFormatado = computed(() => {
  return new Intl.NumberFormat("pt-BR", {
    style: "currency",
    currency: "BRL",
  }).format(produto.value.preco);
});
</script>

<template>
  <main>
    <div class="container">
      <div class="image">
        <img :src="produtoAtivo.image" />
      </div>
      <div class="product-details">
        <p>{{ produto.nome }}</p>
        <p>{{ precoFormatado }}</p>
        <div class="info-status">
          <p class="produto-em-estoque" v-if="produtoAtivo.emEstoque">
            Em estoque
          </p>
          <p class="entre-contato" v-if="!produtoAtivo.emEstoque">
            Me notifique quando estiver dispon√≠vel
          </p>
          <p v-if="produtoAtivo.emEstoque">
            Quantidade: {{ produtoAtivo.quantidade }}
          </p>
        </div>
        <div class="colors">
          <span
            v-for="variante in produto.variacoes"
            :key="variante.id"
            class="color"
            :style="`background-color: ${variante.cor}`"
            @click="mudarProdutoAtivo(variante)"
          ></span>
        </div>
        <button
          :class="[{ btn: true }, { 'btn-disabled': !produtoAtivo.emEstoque }]"
          @click="adicionarAoCarrinho"
        >
          Adicionar ao carrinho
        </button>
      </div>
    </div>
  </main>
</template>

